package com.machine.manager;

import org.apache.commons.codec.binary.Base64;

import java.nio.charset.StandardCharsets;

public class Main {
    public static void main(String[] args) {

        String s = "1234567891013";
        //字符串转字节数组
        byte[] array = s.getBytes(StandardCharsets.UTF_8);

        //输出经过base64编码过后的字符串（有特殊字符不适合作为url）
        String writing1 = Base64.encodeBase64String(array);
        System.out.println("编码1：" + writing1);

        //输出经过base64编码过后的字符串（无特殊字符适合作为url）
        String writing2 = Base64.encodeBase64URLSafeString(array);
        System.out.println("编码2：" + writing2);

        //解码1
        System.out.println("解码1：" + new String(new Base64().decode(writing1)));
        //解码2
        System.out.println("解码2：" + new String(new Base64().decode(writing2)));


        String xxx = "";
        System.out.println("解码3：" + new String(new Base64().decode(xxx)));
    }
}

